operation.add:
		//Q: Insert DB columns
		$query = $this->db->query("DESC ".DB_PREFIX."category_description");
    		if (!$query->num_rows) {
			$this->db->query("ALTER TABLE '" . DB_PREFIX . "category_description' ADD 'icon' VARCHAR(255) not null after 'name' default 'fa-close'");

		}
	//insert data     
	$this->db->query("INSERT INTO " . DB_PREFIX . "category_description SET category_id = '" . (int)$category_id . "', language_id = '" . (int)$language_id . "', name = '" . $this->db->escape($value['name']) . "', icon = '" . $this->db->escape($value['icon']) . "', description = '" . $this->db->escape($value['description']) . "', meta_title = '" . $this->db->escape($value['meta_title']) . "', meta_description = '" . $this->db->escape($value['meta_description']) . "', meta_keyword = '" . $this->db->escape($value['meta_keyword']) . "'");
